import { Component } from '@angular/core';
import { FormBuilder, MaxLengthValidator, Validators } from '@angular/forms';
import { CommonService } from '../common.service';

import { Route, Router } from '@angular/router';

@Component({
  selector: 'app-homepage',
  templateUrl: './homepage.component.html',
  styleUrls: ['./homepage.component.css']
})
export class HomepageComponent {



  public allData:any=[]
  public tableData:any=[];
  // imageUrl: string | ArrayBuffer | null = null;

  registrationForm: any;
  selectedValue: number = 20 ;
  imageUrl: string = "https://t4.ftcdn.net/jpg/04/32/62/27/240_F_432622715_W5LJ15OpH5mROEr4qPPTwhXwNDCwlmK7.jpg";
  isFormSubmit: boolean = false;
  states: string[] = ['Maharashtra', 'Punjab', 'Haryana', 'Uttar Pradesh', 'Madhya Pradesh'];
  countries: string[] = ['India', 'Japan', 'Sri Lanka', 'Australia'];
  tags: string[] = [];

  constructor(private fb: FormBuilder , private comser:CommonService , private route :Router) {
    this.registrationForm = this.fb.group({

      firstname: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      // mob: ['', [Validators.required, Validators.maxLength(10),this.]],
      mob: ['', [Validators.required, Validators.maxLength(10), this.validateMobileNumber]],
      age: [0, Validators.required],
      state: ['', Validators.required],
      country: ['', Validators.required],
      address: ['', Validators.required],
      tags: ['', Validators.required],
      newsletter: [false]
    });


this.getData()

  }

  // handleFileInput(event: any): void {
  //   const file = event.target.files[0];
  //   if (file) {
  //     this.imageUrl = URL.createObjectURL(file);
  //   } else {
  //     this.imageUrl = '';
  //   }
  // }


  handleFileInput(event: any): void {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        this.imageUrl = reader.result as string; // Ensure the result is treated as a string
        console.log('Image path:', file.name);
      };
    }
  }






onSubmit() {
  this.isFormSubmit = true;
  if (this.registrationForm.valid) {
    const formData = this.registrationForm.value;
    // this.comser.sendDtApi(formData).subscribe(
    //   response => {
    //     console.log('Data saved successfully:', response);

    //     // this.registrationForm.reset();
    //   },
    //   error => {
    //     console.error('Error saving data:', error);
    //   }
    // );

        this.comser.sendDataService(this.registrationForm.value)

// this.route.navigate(['.profile'])
this.route.navigate(['/profile']);



  } else {

  }

     this.registrationForm.reset();


}




validateMobileNumber(control:any) {
  const mobileNumberPattern = /^\(?(\d{3})\)?[- ]?(\d{3})[- ]?(\d{4})$/;
  if (control.value && !mobileNumberPattern.test(control.value)) {
    return { invalidMobileNumber: true };
  }
  return null;
}














getData(){
  this.comser.getApi().subscribe({
   next: (res:any)=>{
    console.log(res)
    this.tableData=res
   },
   error:(err:any)=>{
    console.log(err)
   }
  })
}

sendData(){


  this.comser.sendDtApi(this.registrationForm.value).toPromise().then((res)=>{
   console.log(res)
   this.tableData=res
  })
 }













  onClick() {
    this.tags.push(this.registrationForm.get('tags').value);
    console.log(this.tags);
  }


  onCancel() {

  }





























  get f() {
    return this.registrationForm.controls;
  }



  onRangeChange(event: Event) {
    const target = event.target as HTMLInputElement;
    this.selectedValue = parseInt(target.value);
  }














}
